import React, { useState } from 'react';
import './App.css';
import InputField from './Components/InputField';

function App() {
  const [listValue, setListValue] = useState([]);

const addvalue=(item)=>{
  setListValue(...listValue,item)
}

  return (
    <div className='app'>
      <div className='heading'>
        <h2>to-do list</h2>
      </div>
      <InputField upadateList={addvalue}/>
    </div>
  )
}

export default App;
