  };
  return (
    <div  className='listItem'>
      <div className='chbx' onChange={checked} className={``}>
         <input type='checkbox'  name={`checkbox-${props.index}`}/>
      </div> 
        <li>{props.value}</li>
    </div>
  )
}

export default ListItem;
