import React from 'react';

const delay = ms =>Promise(
  resolve => setTimeout(resolve,ms)
)

function ListItem(props) {

  const checked = async event=>{
    await setTimeout(500)
    props.delItem(props.index)
  };
  return (
    <div  className='listItem'>
      <div className='chbx'>
         <input type='checkbox' onChange={checked}  value={`checked-${props.index}`} name={`checkbox-${props.index}`}/>
      </div> 
        <li>{props.value}</li>
    </div>
  )
}

export default ListItem;
