import React, { useState } from 'react';

function ListItem(props) {
  const [check, setCheck] = useState("check")

  const checked=(e)=>{   
    props.delItem(props.index)
    setCheck("checked")
    setIsChecked(false)
  };
  return (
    <div  className={`listItem ${check}`}>
      <div className='chbx' >
         <input type='radio' onClick={checked} value={`checkbox-${props.index}`}  name='checkbox'/>
      </div> 
        <li>{props.value}</li>
    </div>
  )
}

export default ListItem;
